package it.cnr.iit.ucsdht;

import it.cnr.iit.ucs.pip.PIPBase;
import org.eclipse.jetty.server.Server;
import org.eclipse.jetty.server.ServerConnector;
import org.eclipse.jetty.websocket.server.WebSocketHandler;
import org.eclipse.jetty.websocket.servlet.WebSocketServletFactory;
import org.junit.After;
import org.junit.Before;
import org.junit.Test;

import java.io.File;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertTrue;

public class TestUCSInitialization {
    private static final int TEST_SERVER_PORT = 3001;
    private static final  String TEST_SERVER_HOST = "localhost";

    private Server server;

    @Before
    public void setUp() throws Exception {

        // Run the test server endpoint
        startTestServer(TEST_SERVER_HOST, TEST_SERVER_PORT);

    }

    @After
    public void teardown() throws Exception {
        // Stop the server
        server.stop();
        server.destroy();
    }

    // Start the test WebSocket server
    private void startTestServer(String host, int port) throws Exception {
        server = new Server();

        // Create a WebSocket server connector with the desired URL and port
        ServerConnector connector = new ServerConnector(server);
        connector.setHost(host);
        connector.setPort(port);
        server.addConnector(connector);

        // Add WebSocket handler
        WebSocketHandler wsHandler = new WebSocketHandler() {
            @Override
            public void configure(WebSocketServletFactory factory) {
                factory.register(WebSocketTestServer.class);
            }
        };
        server.setHandler(wsHandler);

        // Start the server
        server.start();
    }

    @Test
    public void testReloadState() {

        // The status that the UCS will download from the server
        StatusMock.getInstance().setResponseToDownloadStatus(
                "{\n" +
                        "  \"Response\":{\n" +
                        "    \"value\":{\n" +
                        "      \"topic_name\":\"SIFIS:UCS\",\n" +
                        "      \"topic_uuid\":\"status\",\n" +
                        "      \"value\":{\n" +
                        "        \"database\":\"\",\n" +
                        "        \"pips\":[\n" +
                        "          \"\",\n" +
                        "          \"\"\n" +
                        "        ],\n" +
                        "        \"peps\":[\n" +
                        "          \"\"\n" +
                        "        ],\n" +
                        "        \"policies\":[\n" +
                        "          \"\",\n" +
                        "          \"\"\n" +
                        "        ]\n" +
                        "      }\n" +
                        "    }\n" +
                        "  }\n" +
                        "}"
        );
        // This status includes:
        // - 1 registered PEP
        // - the policies test-policy and example-policy
        // - the PIPs sample-pip and pip-websocket-all-lamps-are-on (monitoring the attribute 'all-lamps-are-on'
        // - an ongoing session in the database

        String[] args = new String[]{"--dht", "ws://" + TEST_SERVER_HOST + ":" + TEST_SERVER_PORT};
        UCSDht.main(args);

        // If the status has been restored correctly, the following should hold:
        assertEquals(2, UCSDht.ucsClient.getProperties().getPipList().size());
        assertEquals(1, UCSDht.ucsClient.getProperties().getPepList().size());

        File testPolicy = new File(UCSDht.policiesDir + File.separator + "test-policy.xml");
        assertTrue(testPolicy.exists());

        assertTrue(UCSDht.ucsClient.getPipRegistry().getByAttributeId("eu:sifis-home:1.0:environment:all-lamps-are-on").isPresent());
        PIPBase pip = (PIPBase) UCSDht.ucsClient.getPipRegistry().getByAttributeId("eu:sifis-home:1.0:environment:all-lamps-are-on").get();
        assertEquals(1, pip.subscriptions.size());
    }
}
